import Head from "next/head";
import React from "react";
import {
    Autocomplete,
    Box,
    Button,
    Card,
    CardContent,
    Container,
    Dialog,
    DialogContent,
    DialogContentText,
    DialogTitle,
    Divider,
    Grid,
    IconButton,
    InputLabel,
    MenuItem,
    Paper,
    Select,
    Snackbar,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TablePagination,
    TableRow,
    TextField,
    Typography,
} from "@mui/material";
// import { products } from "../../__mocks__/products";
import { DashboardLayout } from "../dashboard/dashboard-layout";
import { useEffect, useState } from "react";
import { Delete, Edit, Search } from "@mui/icons-material";
import { useTranslation } from "react-i18next";
import asistenciaServices from "../../services/asistenciaServices";
import ValueServices from "../../services/ValueServices";

const Asistencias = () => {
    const [asistencias, setAsistencias] = useState([]);
    const { t } = useTranslation();

    // Nueva asistencia
    const [anyo, setAnyo] = useState("");
    const [codigo, setCodigo] = useState("");
    const [expediente, setExpediente] = useState([]);
    const [tecnico, setTecnico] = useState([]);
    const [descripcion, setDescripcion] = useState("");
    const [f_alta, setF_alta] = useState("");
    const [f_recepcion, setF_recepcion] = useState("");
    const [f_cumplimentado, setF_cumplimentado] = useState("");
    const [f_limite, setF_limite] = useState("");
    const [f_entrega, setF_entrega] = useState("");
    const [tenico, setTenico] = useState("");
    const [calle, setCalle] = useState("");
    // Nueva asistencia / unidad de obra
    const [cantidad1, setCantidad1] = useState("");
    const [unidad_obra1, setUnidad_obra1] = useState("");

    // Listas
    const [tecnicos, setTecnicos] = useState([]);
    const [expedientes, setExpedientes] = useState([]);
    const [calles, setCalles] = useState([]);
    const [unidades_obra, setUnidades_obra] = useState([]);




    const [open, setOpen] = useState(false);

    const [page, setPage] = useState(0);
    const [rowsPerPage, setRowsPerPage] = useState(10);

    // Cambiar filas por página
    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(parseInt(event.target.value, 10));
        setPage(0);
    };

    const handlePageChange = (event, newPage) => {
        setPage(newPage);
    };

    useEffect(() => {
        asistenciaServices.getAllAsistencias().then((response) => {
            console.log(response.data);
            setAsistencias(response.data);
        });

        asistenciaServices.getAllUnidadesObra().then((response) => {
            setUnidades_obra(response.data);
        });

        ValueServices.getExpedientes().then((response) => {
            setExpedientes(response.data);
        });

        ValueServices.getTecnicos().then((response) => {
            setTecnicos(response.data);
        });

        ValueServices.getCalles().then((response) => {
            setCalles(response.data);
        });


    }, []);

    const handleClose = () => {
        setOpen(false);
    };

    const AsistenciasTable = () => {
        const columns = [" ", "Cantidad", "Unidad de obra", "Precio", "Total"];
        const rows = Array.from({ length: 10 }, (_, i) => i + 1);

        return (
            <Grid container spacing={2}>
                {/* Render the column titles */}
                {columns.map((column) => (
                    <Grid item key={column}>
                        <Typography variant="h6">{column}</Typography>
                    </Grid>
                ))}

                {/* Render the rows */}
                {rows.map((row) => (
                    <React.Fragment key={row}>
                        {/* Render the button column */}
                        <Grid item>
                            <Button variant="contained" color="primary">
                                Button {row}
                            </Button>
                        </Grid>

                        {/* Render the textfield column */}
                        <Grid item>
                            <TextField label={`Textfield ${row}`} />
                        </Grid>

                        {/* Render the select column */}
                        <Grid item>
                            <Select value="" displayEmpty>
                                <MenuItem value="" disabled>
                                    Select {row}
                                </MenuItem>
                                <MenuItem value={1}>Option 1</MenuItem>
                                <MenuItem value={2}>Option 2</MenuItem>
                                <MenuItem value={3}>Option 3</MenuItem>
                            </Select>
                        </Grid>

                        {/* Render the textInput 1 column */}
                        <Grid item>
                            <TextField label={`TextInput 1 ${row}`} />
                        </Grid>

                        {/* Render the textInput 2 column */}
                        <Grid item>
                            <TextField label={`TextInput 2 ${row}`} />
                        </Grid>
                    </React.Fragment>
                ))}
            </Grid>
        );
    };


    const columns = [" ", "Cantidad", "Unidad de obra", "Precio", "Total"];

    // CSS classes for header items
    const headerItemStyles = {
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        color: "white",
        fontWeight: "bold",
        backgroundColor: "#33649e",
        textAlign: "center",
    };



    const [numRows, setNumRows] = useState(2);
    const [rows, setRows] = useState([{ cantidad: "", unidad_obra: null }]);

    const updateRow = (index, updatedRow) => {
        const newRows = [...rows];
        newRows[index] = updatedRow;
        setRows(newRows);
    };

    const handleDeleteRow = (index) => {
        const newRows = [...rows];
        newRows.splice(index, 1);
        setRows(newRows);
        setNumRows(numRows - 1);
    };

    const create_asistencia_component = () => {
        return (
            <Dialog onClose={handleClose} open={open} fullWidth maxWidth="lg">
                <DialogTitle>Crear asistencia</DialogTitle>
                <DialogContent>
                    <DialogContentText>
                        <form>
                            <Grid sx={{ mt: 3 }} container spacing={3}>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        fullWidth
                                        label="Año"
                                        name="anyo"
                                        onChange={(e) => setAnyo(e.target.value)}
                                        required
                                        value={anyo}
                                        type="number"
                                        focused={true}
                                        variant="outlined"
                                        inputProps={{
                                            min: 1970,
                                            max: new Date().getFullYear() + 5,
                                        }}
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        fullWidth
                                        label="Fecha alta:"
                                        name="f_alta"
                                        onChange={(e) => setF_alta(e.target.value)}
                                        required
                                        value={f_alta}
                                        type="date"
                                        focused={true}
                                        variant="outlined"
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        fullWidth
                                        label="Código:"
                                        name="codigo"
                                        onChange={(e) => setCodigo(e.target.value)}
                                        required
                                        value={codigo}
                                        type="text"
                                        focused={true}
                                        variant="outlined"
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        fullWidth
                                        label="Fecha entrega:"
                                        name="f_entrega"
                                        onChange={(e) => setF_entrega(e.target.value)}
                                        required
                                        value={f_entrega}
                                        type="date"
                                        focused={true}
                                        variant="outlined"
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <InputLabel>
                                        Técnico:
                                    </InputLabel>
                                    <Select
                                        onChange={(e) => setTecnico(e.target.value)}
                                        fullWidth
                                        value={tecnico}
                                    >
                                        {tecnicos.map((tecnico) => (
                                            <MenuItem
                                                divider key={tecnico.id} value={tecnico.id}>
                                                {tecnico.apellido}, {tecnico.nombre}
                                            </MenuItem>
                                        ))}
                                    </Select>
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        fullWidth
                                        label="Fecha recepción:"
                                        name="f_recepcion"
                                        onChange={(e) => setF_recepcion(e.target.value)}
                                        required
                                        value={f_recepcion}
                                        type="date"
                                        focused={true}
                                        variant="outlined"
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <InputLabel>
                                        Expediente:
                                    </InputLabel>
                                    <Select
                                        onChange={(e) => setExpediente(e.target.value)}
                                        fullWidth
                                        value={expediente}
                                    >
                                        {expedientes.map((expediente) => (
                                            <MenuItem
                                                divider key={expediente.id} value={expediente.id}>
                                                {expediente.expediente}
                                            </MenuItem>
                                        ))}
                                    </Select>

                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        fullWidth
                                        label="Fecha límite:"
                                        name="f_limite"
                                        onChange={(e) => setF_limite(e.target.value)}
                                        required
                                        value={f_limite}
                                        type="date"
                                        focused={true}
                                        variant="outlined"
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <InputLabel>
                                        Calle:
                                    </InputLabel>
                                    <Autocomplete

                                        fullWidth
                                        onChange={(e, value) => setCalles(value)}
                                        getOptionLabel={(option) =>
                                            option.nomoficial ? option.nomoficial : ""
                                        }
                                        options={calles}
                                        renderOption={(props, option) => (
                                            <Box {...props} key={option.id}>
                                                {option.codtipovia}/ {option.nomoficial} - {option.ultnum}
                                            </Box>
                                        )}
                                        renderInput={(params) => <TextField {...params} />}
                                    />
                                </Grid>
                                <Grid item xs={12} sm={6}>
                                    <TextField
                                        fullWidth
                                        label="Fecha cumplimentado:"
                                        name="f_cumplimentado"
                                        onChange={(e) => setF_cumplimentado(e.target.value)}
                                        required
                                        value={f_cumplimentado}
                                        type="date"
                                        focused={true}
                                        variant="outlined"
                                    />
                                </Grid>
                                <Grid sx={{ pb: 3 }} item xs={12} sm={12}>
                                    <TextField
                                        fullWidth
                                        label="Descripción:"
                                        name="descripcion"
                                        onChange={(e) => setDescripcion(e.target.value)}
                                        required
                                        value={descripcion}
                                        type="text"
                                        focused={true}
                                        variant="outlined"
                                    />
                                </Grid>
                                <Grid item xs={12} sm={0.5} >
                                    {/* Header text */}
                                    <Typography variant="h6"></Typography>
                                </Grid>
                                <Grid item xs={12} sm={2} sx={headerItemStyles}>
                                    <Typography variant="h6">Cantidad</Typography>
                                </Grid>
                                <Grid item xs={12} sm={5.5} sx={headerItemStyles}>
                                    <Typography variant="h6">Unidad de obra</Typography>
                                </Grid>
                                <Grid item xs={12} sm={2} sx={headerItemStyles}>
                                    <Typography variant="h6">Importe</Typography>
                                </Grid>
                                <Grid item xs={12} sm={2} sx={headerItemStyles}>
                                    <Typography variant="h6">Importe total</Typography>
                                </Grid>
                                {Array.from({ length: numRows }, (_, i) => i + 1).map((row, index) => (
                                    <React.Fragment key={row}>
                                        {/* Row */}
                                        <Grid container item xs={12} spacing={3} >
                                            {/* Individual row cells */}
                                            <Grid item xs={12} sm={0.5}>
                                                {/* Red cross icon that is a button */}
                                                <IconButton color="secondary" variant="contained" onClick={() => handleDeleteRow(index)}>
                                                    <Delete />
                                                </IconButton>
                                            </Grid>
                                            <Grid item xs={12} sm={2}>
                                                {/* Textfield only numbers */}
                                                <TextField
                                                    fullWidth
                                                    required
                                                    type="number"
                                                    inputProps={{
                                                        min: 1,
                                                        max: 100,
                                                    }}
                                                    onChange={(e) =>
                                                        updateRow(index, {
                                                            ...rows[index],
                                                            cantidad: e.target.value,
                                                        })
                                                    }
                                                />
                                            </Grid>
                                            <Grid item xs={12} sm={5.5}>
                                                <Autocomplete
                                                    fullWidth
                                                    getOptionLabel={(option) =>
                                                        option.concepto ? option.concepto : ""
                                                    }
                                                    onChange={(e, value) =>
                                                        updateRow(index, {
                                                            ...rows[index],
                                                            unidad_obra: value,
                                                        })
                                                    }
                                                    options={unidades_obra}
                                                    renderOption={(props, option) => (
                                                        <Box {...props} key={option.id}>
                                                            {option.concepto}
                                                        </Box>
                                                    )}
                                                    renderInput={(params) => <TextField {...params} />}
                                                />
                                            </Grid>
                                            <Grid item xs={12} sm={2}>
                                                <TextField
                                                    fullWidth
                                                    name="cantidad"
                                                    required
                                                    type="number"
                                                    inputProps={{
                                                        min: 1,
                                                        max: 100,
                                                    }}
                                                    value={rows[index]?.unidad_obra?.precio || ""}
                                                    disabled
                                                />
                                            </Grid>
                                            <Grid item xs={12} sm={2}>
                                                {/* Value is equal to the precio * cantidad in this row */}
                                                <TextField
                                                    fullWidth
                                                    name="cantidad"
                                                    required
                                                    type="number"
                                                    inputProps={{
                                                        min: 1,
                                                        max: 100,
                                                    }}
                                                    value={
                                                        rows[index]?.unidad_obra?.precio *
                                                        rows[index]?.cantidad
                                                    }
                                                    disabled
                                                />
                                                
                                                
                                            </Grid>
                                        </Grid>
                                    </React.Fragment>
                                ))}
                                <Grid item xs={12} sm={5}></Grid>
                                <Grid item xs={12} sm={2}>
                                    <Button onClick={() => setNumRows(numRows + 1)}>AñADIR FILA</Button>
                                </Grid>
                                <Grid item xs={12} sm={5}></Grid>



                            </Grid>
                        </form>

                    </DialogContentText>
                </DialogContent>
            </Dialog >
        );
    };


    const list_asistencias_component = () => {
        return (
            <Paper
                style={{ borderRadius: "25px" }}
                sx={{ width: "100%", mb: 2 }}
                elevation={10}
            >
                <TableContainer sx={{ minWidth: 128 }} style={{ borderRadius: "25px" }}>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>{t("AÑO")}</TableCell>
                                <TableCell>{t("CÓDIGO")}</TableCell>
                                <TableCell>{t("EXPEDIENTE")}</TableCell>
                                <TableCell>{t("TÉCNICO")}</TableCell>
                                <TableCell>DESC</TableCell>
                                <TableCell>F. ALTA</TableCell>
                                <TableCell>F. RECEP.</TableCell>
                                <TableCell>F. CUMPLI.</TableCell>
                                <TableCell>F. LÍMITE</TableCell>
                                <TableCell>F. ENTREGA</TableCell>
                                <TableCell>TOTAL</TableCell>

                                <TableCell></TableCell>
                            </TableRow>
                        </TableHead>

                        <TableBody>
                            {asistencias
                                .slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                                .map((asistencia, index) => {
                                    return (
                                        <TableRow key={asistencia.id} tabIndex={-1} hover>
                                            <TableCell>{asistencia.anyo}</TableCell>
                                            <TableCell>{asistencia.codigo}</TableCell>
                                            <TableCell>{asistencia.expediente.id}</TableCell>
                                            <TableCell>{asistencia.tecnico.username}</TableCell>
                                            <TableCell>{asistencia.descripcion}</TableCell>
                                            <TableCell>{asistencia.f_recepcion}</TableCell>
                                            <TableCell>{asistencia.f_alta}</TableCell>
                                            <TableCell>{asistencia.f_cumplimentado}</TableCell>
                                            <TableCell>{asistencia.f_limite}</TableCell>
                                            <TableCell>{asistencia.f_entrega}</TableCell>
                                            <TableCell>{asistencia.total}</TableCell>

                                            <TableCell>
                                                <IconButton
                                                    color="primary"
                                                    variant="contained"
                                                >
                                                    <Edit />
                                                </IconButton>
                                                <IconButton
                                                    color="secondary"
                                                    variant="contained"
                                                >
                                                    <Delete />
                                                </IconButton>
                                            </TableCell>
                                            <Divider />
                                        </TableRow>
                                    );
                                })}
                        </TableBody>
                    </Table>
                </TableContainer>
                <TablePagination
                    rowsPerPageOptions={[1, 10, 25, asistencias.length]}
                    component="div"
                    count={asistencias.length}
                    rowsPerPage={rowsPerPage}
                    page={page}
                    onPageChange={handlePageChange}
                    onRowsPerPageChange={handleChangeRowsPerPage}
                />
            </Paper>
        );
    };

    return (
        <DashboardLayout>
            <Head>
                <title>Asistencias</title>
            </Head>
            <Box
                component="main"
                sx={{
                    flexGrow: 1,
                    py: 8,
                }}
            >
                <Container maxWidth={false}>
                    <div>
                        <Box
                            sx={{
                                alignItems: "center",
                                display: "flex",
                                justifyContent: "space-between",
                                flexWrap: "wrap",
                                m: -1,
                            }}
                        >
                            <Typography sx={{ m: 1 }} variant="h4">
                                Asistencias
                            </Typography>
                            <Box sx={{ m: 1 }}>
                                <Button
                                    onClick={() => setOpen(true)}
                                    color="primary"
                                    variant="contained"
                                >
                                    Crear asistencia
                                </Button>
                            </Box>
                        </Box>
                        <Paper sx={{ mb: 5, mt: 2 }} elevation={9}>
                            <Card>
                                <CardContent>
                                    <Box>
                                        <Button
                                            sx={{ mb: 1 }}
                                            variant="contained"
                                        >
                                            <Search />
                                        </Button>
                                        <div
                                            style={{
                                                borderBottom: "2px solid grey",
                                            }}
                                        ></div>
                                    </Box>
                                </CardContent>
                            </Card>
                        </Paper>
                        {list_asistencias_component()}
                    </div>
                </Container>
            </Box>
            {create_asistencia_component()}
        </DashboardLayout>
    );
};

export default Asistencias;
