import Head from "next/head";
import React, { useEffect, useState } from "react";
import PerfectScrollbar from "react-perfect-scrollbar";
import { Upload as UploadIcon } from "../../icons/upload";
import { Download as DownloadIcon } from "../../icons/download";
import { Search as SearchIcon } from "../../icons/search";

import {
  Box,
  Button,
  Card,
  Table,
  TableBody,
  TableCell,
  TableHead,
  TablePagination,
  TableRow,
  Typography,
  CardContent,
  TextField,
  InputAdornment,
  SvgIcon,
  Container,
  Dialog,
  DialogTitle,
  DialogContent,
  Grid,
  IconButton,
  FormControlLabel,
  Checkbox,
  Alert,
} from "@mui/material";
import ValueServices from "../../services/ValueServices";
import { useRouter } from "next/router";
import { DashboardLayout } from "../dashboard/dashboard-layout";
import AuthService from "../../services/auth.service";
import { useNavigate } from "react-router-dom";
import {
  Block,
  Check,
  Delete,
  Edit,
  Visibility,
  VisibilityOff,
} from "@mui/icons-material";
import ReactPasswordChecklist from "react-password-checklist";
import PasswordStrengthBar from "react-password-strength-bar";

const Customers = () => {
  const [page, setPage] = useState(0);
  const [users, setUsers] = useState([]);
  const [rowsPerPage, setRowsPerPage] = useState(10);
  const [openUser, setOpenUser] = useState(false);

  // Usuario
  const [email_user, setEmail_user] = useState("");
  const [password_user, setPassword_user] = useState("");
  const [repeated_password_user, setRepeated_password_user] = useState("");
  const [old_password_user, setOld_password_user] = useState("");
  const [showPassword, setshowPassword] = useState(false);
  const [apellido_user, setApellido_user] = useState("");
  const [nombre_user, setNombre_user] = useState("");
  const [roles_user, setRoles_user] = useState([]);

  const [message, setMessage] = useState("");

  const [editing_user, setEditing_user] = useState(false);
  const [editing_user_id, setEditing_user_id] = useState("");

  const router = useRouter();
  const navigate = useNavigate();

  useEffect(() => {
    const user = AuthService.getCurrentUser();
    if (user) {
      ValueServices.getAllUsers().then((response) => {
        setUsers(response.data);
      });
    } else {
      navigate("/login");
    }
  }, [navigate]);

  const handleRoleCheck = (event) => {
    const { name, checked } = event.target;
    setRoles_user((prevRoles) => {
      if (checked) {
        return [...prevRoles, name];
      } else {
        return prevRoles.filter((role) => role !== name);
      }
    });
  };

  const openEditUser = (e) => {
    setEditing_user_id(e.id);
    setEditing_user(true);
    console.log(e);
    setOpenUser(true);
    setEmail_user(e.email);
    setApellido_user(e.apellido);
    setNombre_user(e.nombre);
    // If e.roles is a json array, extract the name of the roles and asign them to setRoles_user
    setRoles_user(e.roles.map((item) => item.name));
    

    console.log(e.roles.includes("ROLE_ADMIN"));
    console.log(e.roles);
    console.log(e.roles.includes("ROLE_USER"));
    console.log(e.roles.some((item) => item.name === "ROLE_ADMIN"));
    console.log(e.roles.some((item) => item.name === "ROLE_USER"));
  };

  const deleteUser = (id) => {
    ValueServices.deleteUser(id)
      .then((response) => {
        setUsers(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  // Function to activate user based on the id
  const activateUser = (id) => {
    ValueServices.activateUser(id)
      .then((response) => {
        console.log(response.data);
        ValueServices.getAllUsers().then((response) => {
          setUsers(response.data);
        });
      })
      .catch((error) => {
        console.log(error);
      });
  };

  // Function to deactivate user based on the id
  const deactivateUser = (id) => {
    ValueServices.deactivateUser(id)
      .then((response) => {
        console.log(response.data);
        ValueServices.getAllUsers().then((response) => {
          setUsers(response.data);
        });
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const createUser = (e) => {
    var user = {
      username: email_user,
      nombre: nombre_user,
      apellido: apellido_user,
      email: email_user,
      password: password_user,
      role: roles_user,
    };

    AuthService.register(user)
      .then((response) => {
        console.log(response.data);
        setOpenUser(false);
        ValueServices.getAllUsers().then((response) => {
          setUsers(response.data);
        });
      })
      .catch((error) => {
        console.log(error);
      });

    console.log(user);
  };

  const editUser = (e) => {
    var user = {
      username: email_user,
      nombre: nombre_user,
      apellido: apellido_user,
      email: email_user,
      password: password_user,
      oldpassword: old_password_user,
      role: roles_user,
    };
    AuthService.updateUser(editing_user_id, user).then(
      (response) => {
        console.log(response.data);
        setOpenUser(false);
        ValueServices.getAllUsers().then((response) => {
          setUsers(response.data);
        });
      },
      (error) => {
        const resMessage =
          (error.response &&
            error.response.data &&
            error.response.data.message) ||
          error.message ||
          error.toString();
        setMessage(resMessage);
      }
    );

    console.log(user);
  };

  const handleClose = (e) => {
    setOpenUser(false);
    setEmail_user("");
    setApellido_user("");
    setNombre_user("");
    setRoles_user([]);
    setPassword_user("");
    setOld_password_user("");
    setRepeated_password_user("");
    setMessage("")
  };

  const openUser_ = (e) => {
    setEditing_user(false);
    setOpenUser(true);
  };

  // Cambiar filas por pÃ¡gina
  const handleChangeRowsPerPage = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const handlePageChange = (event, newPage) => {
    setPage(newPage);
  };

  const findById = (id) => {
    ValueServices.getUserById(id)
      .then((response) => {
        console.log(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <DashboardLayout>
      <Head>
        <title>Customers | Material Kit</title>
      </Head>
      <Box
        component="main"
        sx={{
          flexGrow: 1,
          py: 8,
        }}
      >
        <Container maxWidth={false}>
          <div>
            <Box sx={{ mb: 3 }}>
              <Box
                sx={{
                  alignItems: "center",
                  display: "flex",
                  justifyContent: "space-between",
                  flexWrap: "wrap",
                  m: -1,
                }}
              >
                <Typography sx={{ m: 1 }} variant="h4">
                  Usuarios
                </Typography>
                <Box sx={{ m: 1 }}>
                  <Button
                    onClick={(e) => openUser_(e)}
                    color="primary"
                    variant="contained"
                  >
                    Crear usuario
                  </Button>
                </Box>
              </Box>
              <Box>
                <Card>
                  <CardContent>
                    <Box sx={{ maxWidth: 500 }}>
                      <TextField
                        fullWidth
                        InputProps={{
                          startAdornment: (
                            <InputAdornment position="start">
                              <SvgIcon color="action" fontSize="small">
                                <SearchIcon />
                              </SvgIcon>
                            </InputAdornment>
                          ),
                        }}
                        placeholder="Buscar usuario"
                        variant="outlined"
                      />
                    </Box>
                  </CardContent>
                </Card>
              </Box>
            </Box>
            <Card>
              <PerfectScrollbar>
                <Box sx={{ minWidth: 1050 }}>
                  <Table>
                    <TableHead>
                      <TableRow>
                        <TableCell>ID</TableCell>
                        <TableCell>Nombre</TableCell>
                        <TableCell>Apellido</TableCell>
                        <TableCell>Email</TableCell>
                        <TableCell>Roles</TableCell>
                        <TableCell></TableCell>
                      </TableRow>
                    </TableHead>

                    <TableBody>
                      {users
                        .slice(
                          page * rowsPerPage,
                          page * rowsPerPage + rowsPerPage
                        )
                        .map((user, index) => {
                          return (
                            // If user.activo is true the TableRow will be normal but if not it will be grey

                            <TableRow
                              key={user.id}
                              style={
                                user.activo
                                  ? null
                                  : { backgroundColor: "#e0e0e0" }
                              }
                              hover
                              onClick={(e) => findById(user.id)}
                            >
                              <TableCell>{user.id}</TableCell>
                              <TableCell>{user.nombre}</TableCell>
                              <TableCell>{user.apellido}</TableCell>
                              <TableCell>{user.email}</TableCell>
                              <TableCell>
                                {user.roles.map((role) => (
                                  <div key={role.name}>{role.name}</div>
                                ))}
                              </TableCell>
                              <TableCell>
                                <IconButton
                                  onClick={(e) => {
                                    alert("Editar usuario " + user.id);
                                    openEditUser(user);
                                  }}
                                  color="primary"
                                  variant="contained"
                                >
                                  <Edit />
                                </IconButton>
                                <IconButton
                                  onClick={(e) => {
                                    alert("Eliminar usuario " + user.id);
                                    deleteUser(user.id);
                                  }}
                                  color="secondary"
                                  variant="contained"
                                >
                                  <Delete />
                                </IconButton>
                                {/* If user.activo is true then it will show a block button but if not it will show an habilitate button */}
                                {user.activo ? (
                                  <IconButton
                                    onClick={(e) => {
                                      alert("Deshabilitar usuario " + user.id);
                                      deactivateUser(user.id);
                                    }}
                                    color="primary"
                                    variant="contained"
                                  >
                                    <Block />
                                  </IconButton>
                                ) : (
                                  <IconButton
                                    onClick={(e) => {
                                      alert("Habilitar usuario " + user.id);
                                      activateUser(user.id);
                                    }}
                                    color="primary"
                                    variant="contained"
                                  >
                                    <Check />
                                  </IconButton>
                                )}
                              </TableCell>
                            </TableRow>
                          );
                        })}
                    </TableBody>
                  </Table>
                </Box>
              </PerfectScrollbar>
              <TablePagination
                rowsPerPageOptions={[1, 10, 25, users.length]}
                component="div"
                count={users.length}
                rowsPerPage={rowsPerPage}
                page={page}
                onPageChange={handlePageChange}
                onRowsPerPageChange={handleChangeRowsPerPage}
              />
            </Card>
          </div>
        </Container>
      </Box>

      <Dialog open={openUser} onClose={handleClose}>
        <DialogTitle>Nuevo Usuario</DialogTitle>
        <DialogContent>
          <Box component="form" noValidate onSubmit={createUser} sx={{ mt: 3 }}>
            <Grid container spacing={2}>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  label="Nombre"
                  autoFocus
                  value={nombre_user}
                  onChange={(e) => setNombre_user(e.target.value)}
                />
              </Grid>
              <Grid item xs={12} sm={6}>
                <TextField
                  required
                  fullWidth
                  label="Apellido(s)"
                  value={apellido_user}
                  onChange={(e) => setApellido_user(e.target.value)}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  margin="normal"
                  required
                  fullWidth
                  label="Nombre de usuario"
                  name="email"
                  autoComplete="email"
                  value={email_user}
                  onChange={(e) => setEmail_user(e.target.value)}
                  error={email_user.length === 0}
                  helperText={
                    email_user.length === 0
                      ? "Debes introducir un nombre de usuario"
                      : ""
                  }
                />
              </Grid>
              {/* If the editing var is true it will add an aditional grid and textfield to verify old password */}
              {editing_user ? (
                <Grid item xs={12}>
                  <TextField
                    required
                    fullWidth
                    label="ContraseÃ±a Antigua"
                    type={showPassword ? "text" : "password"}
                    autoComplete="old-password"
                    value={old_password_user}
                    onChange={(e) => setOld_password_user(e.target.value)}
                    error={old_password_user.length === 0}
                    helperText={
                      password_user.length === 0
                        ? "Debes introducir la contraseÃ±a antigua"
                        : ""
                    }
                    InputProps={{
                      endAdornment: (
                        <InputAdornment position="end">
                          <IconButton
                            onClick={(e) => setshowPassword(!showPassword)}
                            edge="end"
                          >
                            {showPassword ? <VisibilityOff /> : <Visibility />}
                          </IconButton>
                        </InputAdornment>
                      ),
                    }}
                  />
                </Grid>
              ) : null}

              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  label={editing_user ? "Nueva contraseÃ±a" : "ContraseÃ±a"}
                  type={showPassword ? "text" : "password"}
                  autoComplete="new-password"
                  value={password_user}
                  onChange={(e) => setPassword_user(e.target.value)}
                  error={
                    password_user.length === 0 || password_user.length <= 8
                  }
                  helperText={
                    password_user.length === 0 || password_user.length <= 8
                      ? "La contraseÃ±a debe tener al menos 20 caracteres"
                      : ""
                  }
                  InputProps={{
                    endAdornment: (
                      <InputAdornment position="end">
                        <IconButton
                          onClick={(e) => setshowPassword(!showPassword)}
                          edge="end"
                        >
                          {showPassword ? <VisibilityOff /> : <Visibility />}
                        </IconButton>
                      </InputAdornment>
                    ),
                  }}
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  required
                  fullWidth
                  label="Repita la contraseÃ±a"
                  type={showPassword ? "text" : "password"}
                  autoComplete="new-password"
                  value={repeated_password_user}
                  error={password_user !== repeated_password_user}
                  onChange={(e) => setRepeated_password_user(e.target.value)}
                  helperText={
                    password_user !== repeated_password_user
                      ? "Las contraseÃ±as no coinciden"
                      : ""
                  }
                  InputProps={{
                    endAdornment: (
                      <InputAdornment position="end">
                        <IconButton
                          onClick={(e) => setshowPassword(!showPassword)}
                          edge="end"
                        >
                          {showPassword ? <VisibilityOff /> : <Visibility />}
                        </IconButton>
                      </InputAdornment>
                    ),
                  }}
                />
                <ReactPasswordChecklist
                  rules={[
                    "minLength",
                    "specialChar",
                    "number",
                    "capital",
                    "match",
                  ]}
                  minLength={20}
                  value={password_user}
                  valueAgain={repeated_password_user}
                  messages={{
                    minLength: "La contraseÃ±a tiene mÃ¡s de 20 caracteres.",
                    specialChar: "La contraseÃ±a tiene caracteres especiales.",
                    number: "La contraseÃ±a tiene un nÃºmero.",
                    capital: "La contraseÃ±a tiene una letra mayÃºscula.",
                    match: "Las contraseÃ±as coinciden.",
                  }}
                />
                <PasswordStrengthBar password={password_user} />
              </Grid>
            </Grid>

            <FormControlLabel
              control={
                <Checkbox
                  checked={
                    roles_user.some((item) => item.name === "ROLE_ADMIN") ||
                    roles_user.includes("ROLE_ADMIN")
                  }
                  onChange={(e) => {
                    if (e.target.checked) {
                      if (!roles_user.includes("ROLE_ADMIN")) {
                        setRoles_user([...roles_user, "ROLE_ADMIN"]);
                      }
                    } else {
                      if (roles_user.includes("ROLE_ADMIN")) {
                        setRoles_user(
                          roles_user.filter((item) => item !== "ROLE_ADMIN")
                        );
                      }
                      if (
                        roles_user.some((item) => item.name === "ROLE_ADMIN")
                      ) {
                        setRoles_user(
                          roles_user.filter(
                            (item) => item.name !== "ROLE_ADMIN"
                          )
                        );
                      }
                    }
                  }}
                  name="checkedA"
                />
              }
              label="Administrador"
            />
            <FormControlLabel
              control={
                <Checkbox
                  checked={
                    roles_user.some((item) => item.name === "ROLE_USER") ||
                    roles_user.includes("ROLE_USER")
                  }
                  onChange={(e) => {
                    if (e.target.checked) {
                      console.log("CHECKED");
                      if (!roles_user.includes("ROLE_USER")) {
                        setRoles_user([...roles_user, "ROLE_USER"]);
                      }
                    } else {
                      console.log("UNCHECKED");
                      if (roles_user.includes("ROLE_USER")) {
                        setRoles_user(
                          roles_user.filter((item) => item !== "ROLE_USER")
                        );
                      }
                      if (
                        roles_user.some((item) => item.name === "ROLE_USER")
                      ) {
                        setRoles_user(
                          roles_user.filter((item) => item.name !== "ROLE_USER")
                        );
                      }
                    }
                  }}
                  name="checkedB"
                />
              }
              label="Usuario"
            />

            <Button
              onClick={editing_user ? editUser : createUser}
              fullWidth
              variant="contained"
              sx={{ mt: 2, mb: 2 }}
            >
              {editing_user ? "EDITAR USUARIO" : "CREAR USUARIO"}
            </Button>
          </Box>
          {message && <Alert severity="error">{message}</Alert>}
        </DialogContent>
      </Dialog>
    </DashboardLayout>
  );
};

export default Customers;
